     1→import type { CandleData } from '../types/coinbase';
     2→
     3→const DB_NAME = 'CandleCache';
     4→const DB_VERSION = 1;
     5→const STORE_NAME = 'candles';
     6→
     7→export class IndexedDBCache {
     8→  private db: IDBDatabase | null = null;
     9→  private readonly TTL_RECENT = 3600000; // 1 hour in ms
    10→  private readonly TTL_OLD = 86400000; // 24 hours in ms
    11→  private readonly MAX_CANDLES_PER_KEY = 1000;
    12→
    13→  constructor() {
    14→    this.initDB();
    15→  }
    16→
    17→  private async initDB(): Promise<void> {
    18→    return new Promise((resolve, reject) => {
    19→      const request = indexedDB.open(DB_NAME, DB_VERSION);
    20→
    21→      request.onerror = () => {
    22→        console.error('Failed to open IndexedDB:', request.error);
    23→        reject(request.error);
    24→      };
    25→
    26→      request.onsuccess = () => {
    27→        this.db = request.result;
    28→        console.log('IndexedDB initialized successfully');
    29→        resolve();
    30→      };
    31→
    32→      request.onupgradeneeded = (event) => {
    33→        const db = (event.target as IDBOpenDBRequest).result;
    34→        
    35→        // Create object store if it doesn't exist
    36→        if (!db.objectStoreNames.contains(STORE_NAME)) {
    37→          const store = db.createObjectStore(STORE_NAME, { keyPath: 'key' });
    38→          store.createIndex('expiry', 'expiry', { unique: false });
    39→          store.createIndex('granularity', 'granularity', { unique: false });
    40→        }
    41→      };
    42→    });
    43→  }
    44→
    45→  // Ensure DB is ready
    46→  private async ensureDB(): Promise<IDBDatabase> {
    47→    if (!this.db) {
    48→      await this.initDB();
    49→    }
    50→    if (!this.db) {
    51→      throw new Error('Failed to initialize IndexedDB');
    52→    }
    53→    return this.db;
    54→  }
    55→
    56→  // Generate cache key for candle data
    57→  private getCandleKey(granularity: number, timeGroup: string): string {
    58→    return `btc:candles:${granularity}:${timeGroup}`;
    59→  }
    60→
    61→  // Generate cache key for latest candle
    62→  private getLatestKey(granularity: number): string {
    63→    return `btc:latest:${granularity}`;
    64→  }
    65→
    66→  // Get time group for a timestamp based on granularity
    67→  private getTimeGroup(timestamp: number, granularity: number): string {
    68→    const date = new Date(timestamp * 1000);
    69→    
    70→    if (granularity <= 300) { // 1m, 5m - group by day
    71→      return date.toISOString().split('T')[0];
    72→    } else if (granularity <= 3600) { // 15m, 1h - group by week
    73→      const weekNum = Math.floor(date.getTime() / (7 * 24 * 60 * 60 * 1000));
    74→      return `w${weekNum}`;
    75→    } else if (granularity <= 21600) { // 6h - group by month
    76→      return `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}`;
    77→    } else { // 1D - group by year
    78→      return `${date.getFullYear()}`;
    79→    }
    80→  }
    81→
    82→  // Get cached candles for a time range
    83→  async getCachedCandles(
    84→    granularity: number,
    85→    startTime: number,
    86→    endTime: number
    87→  ): Promise<CandleData[]> {
    88→    try {
    89→      const db = await this.ensureDB();
    90→      const timeGroups = this.getTimeGroupsInRange(startTime, endTime, granularity);
    91→      const allCandles: CandleData[] = [];
    92→      
    93→      const transaction = db.transaction([STORE_NAME], 'readonly');
    94→      const store = transaction.objectStore(STORE_NAME);
    95→      
    96→      // Fetch all relevant time groups
    97→      const promises = timeGroups.map(group => {
    98→        const key = this.getCandleKey(granularity, group);
    99→        return new Promise<CandleData[]>((resolve) => {
   100→          const request = store.get(key);
   101→          request.onsuccess = () => {
   102→            const result = request.result;
   103→            if (result && result.expiry > Date.now()) {
   104→              resolve(result.candles || []);
   105→            } else {
   106→              resolve([]);
   107→            }
   108→          };
   109→          request.onerror = () => resolve([]);
   110→        });
   111→      });
   112→      
   113→      const results = await Promise.all(promises);
   114→      for (const candles of results) {
   115→        allCandles.push(...candles);
   116→      }
   117→      
   118→      // Filter to requested time range and sort
   119→      return allCandles
   120→        .filter(c => c.time >= startTime && c.time <= endTime)
   121→        .sort((a, b) => a.time - b.time);
   122→    } catch (error) {
   123→      console.error('Error getting cached candles:', error);
   124→      return [];
   125→    }
   126→  }
   127→
   128→  // Set cached candles
   129→  async setCachedCandles(granularity: number, candles: CandleData[]): Promise<void> {
   130→    if (candles.length === 0) return;
   131→    
   132→    try {
   133→      const db = await this.ensureDB();
   134→      
   135→      // Group candles by time group
   136→      const groups = new Map<string, CandleData[]>();
   137→      
   138→      for (const candle of candles) {
   139→        const group = this.getTimeGroup(candle.time, granularity);
   140→        if (!groups.has(group)) {
   141→          groups.set(group, []);
   142→        }
   143→        groups.get(group)!.push(candle);
   144→      }
   145→      
   146→      const transaction = db.transaction([STORE_NAME], 'readwrite');
   147→      const store = transaction.objectStore(STORE_NAME);
   148→      
   149→      for (const [group, groupCandles] of groups) {
   150→        const key = this.getCandleKey(granularity, group);
   151→        
   152→        // Get existing candles
   153→        const getRequest = store.get(key);
   154→        
   155→        await new Promise<void>((resolve, reject) => {
   156→          getRequest.onsuccess = async () => {
   157→            const existing = getRequest.result;
   158→            let existingCandles: CandleData[] = existing?.candles || [];
   159→            
   160→            // Merge with new candles, avoiding duplicates
   161→            const candleMap = new Map<number, CandleData>();
   162→            
   163→            // Add existing candles
   164→            for (const candle of existingCandles) {
   165→              candleMap.set(candle.time, candle);
   166→            }
   167→            
   168→            // Add/update new candles
   169→            for (const candle of groupCandles) {
   170→              candleMap.set(candle.time, candle);
   171→            }
   172→            
   173→            // Sort and limit size
   174→            const mergedCandles = Array.from(candleMap.values())
   175→              .sort((a, b) => a.time - b.time)
   176→              .slice(-this.MAX_CANDLES_PER_KEY);
   177→            
   178→            // Determine TTL based on how recent the data is
   179→            const now = Date.now() / 1000;
   180→            const mostRecent = mergedCandles[mergedCandles.length - 1]?.time || 0;
   181→            const ttl = (now - mostRecent) < 86400 ? this.TTL_RECENT : this.TTL_OLD;
   182→            
   183→            // Store the data
   184→            const putRequest = store.put({
   185→              key,
   186→              granularity,
   187→              candles: mergedCandles,
   188→              expiry: Date.now() + ttl
   189→            });
   190→            
   191→            putRequest.onsuccess = () => resolve();
   192→            putRequest.onerror = () => reject(putRequest.error);
   193→          };
   194→          
   195→          getRequest.onerror = () => reject(getRequest.error);
   196→        });
   197→      }
   198→      
   199→      await new Promise<void>((resolve) => {
   200→        transaction.oncomplete = () => resolve();
   201→      });
   202→    } catch (error) {
   203→      console.error('Error setting cached candles:', error);
   204→    }
   205→  }
   206→
   207→  // Update the latest candle
   208→  async updateLatestCandle(granularity: number, candle: CandleData): Promise<void> {
   209→    try {
   210→      const db = await this.ensureDB();
   211→      const key = this.getLatestKey(granularity);
   212→      
   213→      const transaction = db.transaction([STORE_NAME], 'readwrite');
   214→      const store = transaction.objectStore(STORE_NAME);
   215→      
   216→      await new Promise<void>((resolve, reject) => {
   217→        const request = store.put({
   218→          key,
   219→          granularity,
   220→          candles: [candle],
   221→          expiry: Date.now() + 60000 // 1 minute TTL for latest
   222→        });
   223→        
   224→        request.onsuccess = () => resolve();
   225→        request.onerror = () => reject(request.error);
   226→      });
   227→      
   228→      // Also update in the main cache
   229→      await this.setCachedCandles(granularity, [candle]);
   230→    } catch (error) {
   231→      console.error('Error updating latest candle:', error);
   232→    }
   233→  }
   234→
   235→  // Get the latest candle
   236→  async getLatestCandle(granularity: number): Promise<CandleData | null> {
   237→    try {
   238→      const db = await this.ensureDB();
   239→      const key = this.getLatestKey(granularity);
   240→      
   241→      const transaction = db.transaction([STORE_NAME], 'readonly');
   242→      const store = transaction.objectStore(STORE_NAME);
   243→      
   244→      return new Promise((resolve) => {
   245→        const request = store.get(key);
   246→        
   247→        request.onsuccess = () => {
   248→          const result = request.result;
   249→          if (result && result.expiry > Date.now() && result.candles?.length > 0) {
   250→            resolve(result.candles[0]);
   251→          } else {
   252→            resolve(null);
   253→          }
   254→        };
   255→        
   256→        request.onerror = () => resolve(null);
   257→      });
   258→    } catch (error) {
   259→      console.error('Error getting latest candle:', error);
   260→      return null;
   261→    }
   262→  }
   263→
   264→  // Get time groups in a range
   265→  private getTimeGroupsInRange(startTime: number, endTime: number, granularity: number): string[] {
   266→    const groups = new Set<string>();
   267→    let current = startTime;
   268→    
   269→    while (current <= endTime) {
   270→      groups.add(this.getTimeGroup(current, granularity));
   271→      
   272→      // Increment based on granularity
   273→      if (granularity <= 300) {
   274→        current += 86400; // 1 day
   275→      } else if (granularity <= 3600) {
   276→        current += 7 * 86400; // 1 week
   277→      } else if (granularity <= 21600) {
   278→        current += 30 * 86400; // ~1 month
   279→      } else {
   280→        current += 365 * 86400; // 1 year
   281→      }
   282→    }
   283→    
   284→    return Array.from(groups);
   285→  }
   286→
   287→  // Clear all cached data (for debugging)
   288→  async clearCache(): Promise<void> {
   289→    try {
   290→      const db = await this.ensureDB();
   291→      const transaction = db.transaction([STORE_NAME], 'readwrite');
   292→      const store = transaction.objectStore(STORE_NAME);
   293→      
   294→      await new Promise<void>((resolve, reject) => {
   295→        const request = store.clear();
   296→        request.onsuccess = () => {
   297→          console.log('Cache cleared');
   298→          resolve();
   299→        };
   300→        request.onerror = () => reject(request.error);
   301→      });
   302→    } catch (error) {
   303→      console.error('Error clearing cache:', error);
   304→    }
   305→  }
   306→
   307→  // Clean expired entries
   308→  async cleanExpired(): Promise<void> {
   309→    try {
   310→      const db = await this.ensureDB();
   311→      const transaction = db.transaction([STORE_NAME], 'readwrite');
   312→      const store = transaction.objectStore(STORE_NAME);
   313→      const index = store.index('expiry');
   314→      
   315→      const now = Date.now();
   316→      const range = IDBKeyRange.upperBound(now);
   317→      
   318→      const request = index.openCursor(range);
   319→      
   320→      await new Promise<void>((resolve) => {
   321→        request.onsuccess = (event) => {
   322→          const cursor = (event.target as IDBRequest).result;
   323→          if (cursor) {
   324→            store.delete(cursor.primaryKey);
   325→            cursor.continue();
   326→          } else {
   327→            resolve();
   328→          }
   329→        };
   330→        
   331→        request.onerror = () => resolve();
   332→      });
   333→    } catch (error) {
   334→      console.error('Error cleaning expired entries:', error);
   335→    }
   336→  }
   337→
   338→  // Check if cache is available
   339→  isAvailable(): boolean {
   340→    return 'indexedDB' in window;
   341→  }
   342→
   343→  // Close the database connection
   344→  close(): void {
   345→    if (this.db) {
   346→      this.db.close();
   347→      this.db = null;
   348→    }
   349→  }
   350→}
   351→
   352→// Singleton instance
   353→let cacheInstance: IndexedDBCache | null = null;
   354→
   355→export function getIndexedDBCache(): IndexedDBCache {
   356→  if (!cacheInstance) {
   357→    cacheInstance = new IndexedDBCache();
   358→  }
   359→  return cacheInstance;
   360→}

