     1→import pLimit from 'p-limit';
     2→import { getIndexedDBCache } from './indexedDBCache';
     3→import { CoinbaseAPI } from './coinbaseApi';
     4→import type { CandleData } from '../types/coinbase';
     5→
     6→interface LoadConfig {
     7→  granularity: number;
     8→  days: number;
     9→  priority: number;
    10→}
    11→
    12→export class HistoricalDataLoader {
    13→  private api: CoinbaseAPI;
    14→  private cache = getIndexedDBCache();
    15→  private isLoading = false;
    16→  private loadInterval: number | null = null;
    17→  private updateInterval: number | null = null;
    18→  
    19→  // Concurrent request limiter (3 requests at a time to avoid rate limits)
    20→  private limit = pLimit(3);
    21→  
    22→  // Loading configuration for different granularities
    23→  private readonly loadConfigs: LoadConfig[] = [
    24→    { granularity: 60, days: 7, priority: 1 },      // 1m - last week
    25→    { granularity: 300, days: 30, priority: 2 },    // 5m - last month
    26→    { granularity: 900, days: 90, priority: 3 },    // 15m - last 3 months
    27→    { granularity: 3600, days: 365, priority: 4 },  // 1h - last year
    28→    { granularity: 21600, days: 730, priority: 5 }, // 6h - last 2 years
    29→    { granularity: 86400, days: 1825, priority: 6 } // 1D - last 5 years
    30→  ];
    31→
    32→  constructor() {
    33→    this.api = new CoinbaseAPI();
    34→  }
    35→
    36→  // Start the background loader
    37→  async start(): Promise<void> {
    38→    console.log('Starting historical data loader...');
    39→    
    40→    // Initial load
    41→    await this.loadAllHistoricalData();
    42→    
    43→    // Update latest candles every minute
    44→    this.updateInterval = window.setInterval(() => {
    45→      this.updateLatestCandles();
    46→    }, 60000);
    47→    
    48→    // Reload historical data every hour to fill any gaps
    49→    this.loadInterval = window.setInterval(() => {
    50→      this.loadAllHistoricalData();
    51→    }, 3600000);
    52→    
    53→    // Clean expired cache entries every 30 minutes
    54→    window.setInterval(() => {
    55→      this.cache.cleanExpired();
    56→    }, 1800000);
    57→  }
    58→
    59→  // Stop the background loader
    60→  stop(): void {
    61→    if (this.loadInterval) {
    62→      window.clearInterval(this.loadInterval);
    63→      this.loadInterval = null;
    64→    }
    65→    
    66→    if (this.updateInterval) {
    67→      window.clearInterval(this.updateInterval);
    68→      this.updateInterval = null;
    69→    }
    70→  }
    71→
    72→  // Load all historical data based on priority
    73→  async loadAllHistoricalData(): Promise<void> {
    74→    if (this.isLoading) {
    75→      console.log('Historical data load already in progress, skipping...');
    76→      return;
    77→    }
    78→    
    79→    this.isLoading = true;
    80→    console.log('Loading all historical data...');
    81→    
    82→    try {
    83→      // Sort by priority and load
    84→      const sortedConfigs = [...this.loadConfigs].sort((a, b) => a.priority - b.priority);
    85→      
    86→      for (const config of sortedConfigs) {
    87→        await this.loadHistoricalDataForGranularity(config);
    88→        
    89→        // Small delay between granularities
    90→        await new Promise(resolve => setTimeout(resolve, 1000));
    91→      }
    92→      
    93→      console.log('Historical data load complete!');
    94→    } catch (error) {
    95→      console.error('Error loading historical data:', error);
    96→    } finally {
    97→      this.isLoading = false;
    98→    }
    99→  }
   100→
   101→  // Load historical data for a specific granularity
   102→  private async loadHistoricalDataForGranularity(config: LoadConfig): Promise<void> {
   103→    console.log(`Loading ${config.days} days of ${config.granularity}s data...`);
   104→    
   105→    const endTime = new Date();
   106→    const batchSize = 300; // Coinbase limit
   107→    const totalSeconds = config.days * 24 * 60 * 60;
   108→    const totalCandles = Math.ceil(totalSeconds / config.granularity);
   109→    const batches = Math.ceil(totalCandles / batchSize);
   110→    
   111→    // Check what we already have in cache
   112→    const startTime = new Date(endTime.getTime() - totalSeconds * 1000);
   113→    const cachedCandles = await this.cache.getCachedCandles(
   114→      config.granularity,
   115→      Math.floor(startTime.getTime() / 1000),
   116→      Math.floor(endTime.getTime() / 1000)
   117→    );
   118→    
   119→    console.log(`Found ${cachedCandles.length} cached candles for ${config.granularity}s`);
   120→    
   121→    // Find gaps in the data
   122→    const gaps = this.findDataGaps(cachedCandles, config.granularity, startTime, endTime);
   123→    
   124→    if (gaps.length === 0) {
   125→      console.log(`No gaps found for ${config.granularity}s data`);
   126→      return;
   127→    }
   128→    
   129→    console.log(`Found ${gaps.length} gaps to fill for ${config.granularity}s`);
   130→    
   131→    // Load data for each gap with concurrency limit
   132→    const loadPromises = gaps.map(gap => 
   133→      this.limit(() => this.loadDataForGap(config.granularity, gap.start, gap.end))
   134→    );
   135→    
   136→    await Promise.all(loadPromises);
   137→    console.log(`Completed loading ${config.granularity}s data`);
   138→  }
   139→
   140→  // Find gaps in cached data
   141→  private findDataGaps(
   142→    candles: CandleData[], 
   143→    granularity: number, 
   144→    startTime: Date, 
   145→    endTime: Date
   146→  ): Array<{start: Date, end: Date}> {
   147→    const gaps: Array<{start: Date, end: Date}> = [];
   148→    
   149→    if (candles.length === 0) {
   150→      // No data at all
   151→      gaps.push({ start: startTime, end: endTime });
   152→      return gaps;
   153→    }
   154→    
   155→    // Sort candles by time
   156→    const sorted = [...candles].sort((a, b) => a.time - b.time);
   157→    
   158→    // Check for gap at the beginning
   159→    if (sorted[0].time > Math.floor(startTime.getTime() / 1000) + granularity) {
   160→      gaps.push({
   161→        start: startTime,
   162→        end: new Date(sorted[0].time * 1000 - granularity * 1000)
   163→      });
   164→    }
   165→    
   166→    // Check for gaps in the middle
   167→    for (let i = 0; i < sorted.length - 1; i++) {
   168→      const expectedNext = sorted[i].time + granularity;
   169→      const actualNext = sorted[i + 1].time;
   170→      
   171→      // Allow for some wiggle room (5% of granularity)
   172→      if (actualNext > expectedNext * 1.05) {
   173→        gaps.push({
   174→          start: new Date(expectedNext * 1000),
   175→          end: new Date((actualNext - granularity) * 1000)
   176→        });
   177→      }
   178→    }
   179→    
   180→    // Check for gap at the end
   181→    const lastCandle = sorted[sorted.length - 1];
   182→    if (lastCandle.time < Math.floor(endTime.getTime() / 1000) - granularity) {
   183→      gaps.push({
   184→        start: new Date((lastCandle.time + granularity) * 1000),
   185→        end: endTime
   186→      });
   187→    }
   188→    
   189→    // Limit gaps to reasonable sizes (max 300 candles per gap)
   190→    const splitGaps: Array<{start: Date, end: Date}> = [];
   191→    
   192→    for (const gap of gaps) {
   193→      const gapSeconds = (gap.end.getTime() - gap.start.getTime()) / 1000;
   194→      const gapCandles = Math.ceil(gapSeconds / granularity);
   195→      
   196→      if (gapCandles <= 300) {
   197→        splitGaps.push(gap);
   198→      } else {
   199→        // Split large gaps
   200→        const numSplits = Math.ceil(gapCandles / 300);
   201→        const splitSize = gapSeconds / numSplits;
   202→        
   203→        for (let i = 0; i < numSplits; i++) {
   204→          splitGaps.push({
   205→            start: new Date(gap.start.getTime() + i * splitSize * 1000),
   206→            end: new Date(Math.min(
   207→              gap.start.getTime() + (i + 1) * splitSize * 1000,
   208→              gap.end.getTime()
   209→            ))
   210→          });
   211→        }
   212→      }
   213→    }
   214→    
   215→    return splitGaps;
   216→  }
   217→
   218→  // Load data for a specific gap
   219→  private async loadDataForGap(granularity: number, start: Date, end: Date): Promise<void> {
   220→    try {
   221→      console.log(`Loading gap: ${start.toISOString()} to ${end.toISOString()} (${granularity}s)`);
   222→      
   223→      const candles = await this.api.getCandles(
   224→        'BTC-USD',
   225→        granularity,
   226→        start.toISOString(),
   227→        end.toISOString()
   228→      );
   229→      
   230→      if (candles.length > 0) {
   231→        await this.cache.setCachedCandles(granularity, candles);
   232→        console.log(`Cached ${candles.length} candles for ${granularity}s`);
   233→      }
   234→      
   235→      // Small delay to avoid rate limiting
   236→      await new Promise(resolve => setTimeout(resolve, 200));
   237→    } catch (error) {
   238→      console.error(`Error loading gap for ${granularity}s:`, error);
   239→      // Don't throw - continue with other gaps
   240→    }
   241→  }
   242→
   243→  // Update latest candles for all granularities
   244→  private async updateLatestCandles(): Promise<void> {
   245→    console.log('Updating latest candles...');
   246→    
   247→    const updatePromises = this.loadConfigs.map(config =>
   248→      this.limit(async () => {
   249→        try {
   250→          // Get the last few candles
   251→          const candles = await this.api.getCandles(
   252→            'BTC-USD',
   253→            config.granularity
   254→          );
   255→          
   256→          if (candles.length > 0) {
   257→            // Update cache with latest candles
   258→            await this.cache.setCachedCandles(config.granularity, candles.slice(-10));
   259→            
   260→            // Update the latest candle marker
   261→            const latest = candles[candles.length - 1];
   262→            await this.cache.updateLatestCandle(config.granularity, latest);
   263→          }
   264→        } catch (error) {
   265→          console.error(`Error updating latest candles for ${config.granularity}s:`, error);
   266→        }
   267→      })
   268→    );
   269→    
   270→    await Promise.all(updatePromises);
   271→    console.log('Latest candles updated');
   272→  }
   273→
   274→  // Get loading status
   275→  getStatus(): { isLoading: boolean; cacheAvailable: boolean } {
   276→    return {
   277→      isLoading: this.isLoading,
   278→      cacheAvailable: this.cache.isAvailable()
   279→    };
   280→  }
   281→}
   282→
   283→// Singleton instance
   284→let loaderInstance: HistoricalDataLoader | null = null;
   285→
   286→export function getHistoricalDataLoader(): HistoricalDataLoader {
   287→  if (!loaderInstance) {
   288→    loaderInstance = new HistoricalDataLoader();
   289→  }
   290→  return loaderInstance;
   291→}

