    22→export class CompoundEngine {
    23→  private config: VaultAllocationConfig;
    24→  private state: CompoundState;
    25→  private transactions: CompoundTransaction[] = [];
    26→  
    27→  constructor(config: VaultAllocationConfig, initialState?: CompoundState) {
    28→    this.config = config;
    29→    this.state = initialState || {
    30→      btcVault: 0,
    31→      usdGrowth: 0,
    32→      usdcVault: 0,
    33→      totalCompounded: 0,
    34→      lastCompoundTime: Date.now(),
    35→      compoundCount: 0
    36→    };
    37→  }
    38→  
    39→  shouldCompound(profit: number, currentTime: number): boolean {
    40→    if (!this.config.autoCompound) return false;
    41→    if (profit < this.config.minCompoundAmount) return false;
    42→    
    43→    switch (this.config.compoundFrequency) {
    44→      case 'trade':
    45→        return true;
    46→      case 'daily':
    47→        return currentTime - this.state.lastCompoundTime >= 24 * 60 * 60 * 1000;
    48→      case 'weekly':
