   401→  private processSellSignal(signal: any, candle: CandleData, state: StrategyState): void {
   402→    const size = signal.size || this.strategy.getTotalPositionSize();
   403→    if (size <= 0) return;
   404→    
   405→    // Double-check we have enough BTC to sell
   406→    if (size > state.balance.btcPositions) {
   407→      console.error(`Critical: Attempted to sell ${size.toFixed(6)} BTC but only have ${state.balance.btcPositions.toFixed(6)} BTC`);
   408→      return;
   409→    }
   410→
   411→    // Apply slippage
   412→    const executionPrice = candle.close * (1 - this.config.slippage / 100);
   413→    const proceeds = size * executionPrice;
   414→    
   415→    // Use taker fee for market orders (sells are typically market orders in backtesting)
   416→    const feePercent = this.config.takerFeePercent || this.config.feePercent || 0.75;
   417→    const grossFee = proceeds * (feePercent / 100);
   418→    const feeRebate = grossFee * ((this.config.feeRebatePercent || 0) / 100);
   419→    const netFee = grossFee - feeRebate;
   420→    
   421→    const netProceeds = proceeds - netFee;
   422→    this.totalFeesCollected += grossFee;
   423→    this.totalFeeRebates += feeRebate;
   424→
   425→    // Calculate profit
   426→    const positions = this.strategy.getPositions();
   427→    let totalCost = 0;
   428→    let remainingSize = size;
   429→
   430→    // FIFO position closing
   431→    const closedPositions = [];
   432→    for (const position of positions) {
   433→      if (remainingSize <= 0) break;
   434→      
   435→      const closeSize = Math.min(remainingSize, position.size);
   436→      // Use the actual cost per BTC if available (includes buy fees)
   437→      const costPerBtc = position.metadata?.actualCostPerBtc || position.entryPrice;
   438→      totalCost += closeSize * costPerBtc;
   439→      remainingSize -= closeSize;
   440→      
   441→      if (closeSize === position.size) {
   442→        closedPositions.push(position);
   443→      }
   444→    }
   445→
   446→    const profit = netProceeds - totalCost;
   447→    const profitPercent = (profit / totalCost) * 100;
   448→    
   449→    console.log(`[BacktestEngine] Sell profit calculation:`, {
   450→      sellPrice: executionPrice,
   451→      size: size,
   452→      grossProceeds: proceeds,
   453→      sellFees: netFee,
   454→      netProceeds: netProceeds,
   455→      totalCostWithBuyFees: totalCost,
   456→      profit: profit,
   457→      profitPercent: profitPercent,
   458→      timestamp: new Date(candle.time * 1000).toISOString()
   459→    });
   460→
   461→    // Compound profit distribution
   462→    if (profit > 0) {
   463→      // Return principal to USD first
   464→      state.balance.usd += totalCost;
   465→      
   466→      // Check if we should compound
