   473→        state.balance.btcVault = compoundState.btcVault;
   474→        state.balance.usd += compoundState.usdGrowth; // Add USD growth to trading balance
   475→        state.balance.vault = compoundState.usdcVault;
   476→        
   477→        // Track the USD growth separately
   478→        this.initialBalanceGrowth = compoundState.usdGrowth;
   479→        
   480→        console.log(`[BacktestEngine] Compound transaction executed:`, {
   481→          profit: profit.toFixed(2),
   482→          btcAllocation: transaction.btcAllocation.toFixed(2),
   483→          usdAllocation: transaction.usdAllocation.toFixed(2),
   484→          usdcAllocation: transaction.usdcAllocation.toFixed(2),
   485→          btcReceived: (transaction.btcAllocation / executionPrice).toFixed(6),
   486→          compoundCount: compoundState.compoundCount,
   487→          totalCompounded: compoundState.totalCompounded.toFixed(2)
   488→        });
   489→        
   490→        const metrics = this.compoundEngine.getMetrics(executionPrice);
