   222→        if (params.ratioMultiplier === 1) {
   223→          amount = params.basePositionAmount * level;
   224→        } else {
   225→          const levelRatio = Math.pow(params.ratioMultiplier, level - 1);
   226→          amount = params.basePositionAmount * levelRatio;
   227→        }
   228→      } else {
   229→        const basePercent = params.basePositionPercent / 100;
   230→        if (params.ratioMultiplier === 1) {
   231→          amount = balance * (basePercent * level);
   232→        } else {
   233→          const levelRatio = Math.pow(params.ratioMultiplier, level - 1);
   234→          amount = balance * (basePercent * levelRatio);
   235→        }
   236→      }
   237→      
   238→      sizes.push({
   239→        level,
   240→        amount,
   241→        percentage: (amount / balance) * 100
   242→      });
   243→    }
   244→    
   245→    return sizes;
   246→  }
