     4→/**
     5→ * ULTRA MICRO-SCALPING STRATEGY
     6→ * 
     7→ * Philosophy: Buy every micro dip, sell for tiny profits, never lose because Bitcoin always recovers
     8→ * 
     9→ * Key Features:
    10→ * - Hair-trigger entries: 0.02% drops trigger buys
    11→ * - Minimum viable profits: 0.9% target = 0.075% net after 0.825% fees
    12→ * - YOLO position sizing: 90% on first entry
    13→ * - No stop losses: We wait for recovery
    14→ * - Ultra-fast detection: 3-candle lookback
    15→ * - High frequency: 10-50+ trades per hour possible
    16→ * 
    17→ * Math:
    18→ * - $1000 × 90% = $900 position
    19→ * - 0.9% move = $8.10 gross profit
    20→ * - Minus 0.825% fees = $0.675 net profit per trade
    21→ * - 20 trades/day = $13.50 daily profit (1.35% return)
    22→ */
    23→export interface ReverseRatioConfig extends StrategyConfig {
    24→  initialDropPercent: number;    // % drop from recent high to start buying (default: 5)
    25→  levelDropPercent: number;      // % drop between levels (default: 5)
    26→  ratioMultiplier: number;       // Multiplier for each level (default: 2)
    27→  profitTarget: number;          // % above initial entry to sell all (default: 7)
    28→  maxLevels: number;            // Maximum number of buy levels (default: 5)
    29→  lookbackPeriod: number;       // Candles to look back for recent high (default: 30)
    30→  positionSizeMode: 'percentage' | 'fixed';  // How to calculate position sizes
    31→  basePositionPercent: number;   // Base percentage of balance for first level (if percentage mode)
    32→  basePositionAmount: number;    // Fixed dollar amount for first level (if fixed mode)
    33→  maxPositionPercent: number;    // Max percentage of balance to use across all levels
    34→}
    35→
    36→export class ReverseRatioStrategy extends Strategy {
    37→  private recentHigh: number = 0;
    38→  private initialEntryPrice: number = 0;
