    58→    };
    59→
    60→    super(
    61→      'Ultra Micro-Scalping',
    62→      `Hair-trigger entries on ${fullConfig.initialDropPercent}% dips, exits at ${fullConfig.profitTarget}% for ${(fullConfig.profitTarget - 0.825).toFixed(3)}% net profit`,
    63→      fullConfig
    64→    );
    65→  }
    66→
    67→  analyze(candles: CandleData[], currentPrice: number): Signal {
    68→    const config = this.config as ReverseRatioConfig;
