   304→    
   305→    // Calculate position value in USD based on mode
   306→    let positionValue: number;
   307→    
   308→    if (config.positionSizeMode === 'fixed') {
   309→      // Fixed dollar amount mode
   310→      if (config.ratioMultiplier === 1) {
   311→        // Linear: $50, $100, $150, $200...
   312→        positionValue = config.basePositionAmount * level;
   313→      } else {
