   445→  private updateRecentHigh(candles: CandleData[], currentPrice: number): void {
   446→    const config = this.config as ReverseRatioConfig;
   447→    
   448→    // If we have no positions, track the rolling high from recent candles
   449→    if (this.state.positions.length === 0) {
   450→      // ULTRA SCALPING FIX: Don't wait for lookback period - trade immediately!
   451→      if (this.recentHigh === 0 && candles.length > 0) {
   452→        // Initialize to the first candle's high so we can start trading immediately
   453→        this.recentHigh = candles[0].high;
   454→        console.log(`[ReverseRatio] ULTRA MODE: Initialized to first candle high ${this.recentHigh.toFixed(2)} - ready to trade immediately!`);
   455→      }
   456→      
   457→      // Now update based on all available candles (not just lookback period)
   458→      const allHighs = candles.map(c => c.high);
   459→      const absoluteHigh = Math.max(...allHighs, currentPrice);
   460→      
   461→      // For ultra-scalping, use a rolling window only after we have enough candles
   462→      if (candles.length >= config.lookbackPeriod) {
   463→        const lookback = config.lookbackPeriod;
   464→        const startIndex = Math.max(0, candles.length - lookback);
   465→        const recentCandles = candles.slice(startIndex);
   466→        const lookbackHigh = Math.max(...recentCandles.map(c => c.high));
   467→        
   468→        // Use the lookback high for more responsive trading
   469→        if (lookbackHigh > this.recentHigh || this.recentHigh === 0) {
   470→          this.recentHigh = lookbackHigh;
   471→          console.log(`[ReverseRatio] Updated to ${lookback}-candle high: ${this.recentHigh.toFixed(2)}`);
   472→        }
   473→      } else {
   474→        // Not enough candles for lookback - use absolute high
   475→        this.recentHigh = absoluteHigh;
   476→        console.log(`[ReverseRatio] Using absolute high (only ${candles.length} candles): ${this.recentHigh.toFixed(2)}`);
   477→      }
   478→      
   479→      // Always update if current price is a new high
   480→      if (currentPrice > this.recentHigh) {
   481→        this.recentHigh = currentPrice;
   482→        console.log(`[ReverseRatio] New absolute high: ${this.recentHigh.toFixed(2)}`);
   483→      }
   484→    } else {
   485→      // When in a position, only update if we see a new high
   486→      if (currentPrice > this.recentHigh) {
   487→        this.recentHigh = currentPrice;
   488→        console.log(`[ReverseRatio] New high while in position: ${this.recentHigh.toFixed(2)}`);
   489→      }
   490→    }
   491→  }
