   493→  private calculateRatioSum(levels: number, multiplier: number): number {
   494→    let sum = 0;
   495→    if (multiplier === 1) {
   496→      // Linear progression: 1 + 2 + 3 + ... + n = n(n+1)/2
   497→      sum = (levels * (levels + 1)) / 2;
   498→    } else {
   499→      // Exponential progression
   500→      for (let i = 0; i < levels; i++) {
   501→        sum += Math.pow(multiplier, i);
   502→      }
   503→    }
   504→    return sum;
   505→  }
