   304→    
   305→    // Calculate position value in USD based on mode
   306→    let positionValue: number;
   307→    
   308→    if (config.positionSizeMode === 'fixed') {
   309→      // Fixed dollar amount mode
   310→      if (config.ratioMultiplier === 1) {
   311→        // Linear: $50, $100, $150, $200...
   312→        positionValue = config.basePositionAmount * level;
   313→      } else {
   314→        // Exponential based on ratio multiplier
   315→        const levelRatio = Math.pow(config.ratioMultiplier, level - 1);
   316→        positionValue = config.basePositionAmount * levelRatio;
   317→      }
   318→    } else {
   319→      // Percentage mode (default)
   320→      const basePercent = config.basePositionPercent / 100;
   321→      
   322→      // For ultra micro-scalping with high base percent, use remaining capital approach
   323→      if (basePercent >= 0.8) {
   324→        // Use % of REMAINING capital for subsequent buys
   325→        const remainingCapital = this.state.balance.usd + this.state.balance.vault;
   326→        
   327→        if (level === 1) {
   328→          // First buy uses base percent of total
   329→          positionValue = remainingCapital * basePercent;
   330→        } else {
   331→          // Subsequent buys scale down based on ratio multiplier
   332→          // This ensures we save some capital for later levels
   333→          const levelMultiplier = 1 / Math.pow(config.ratioMultiplier, level - 1);
   334→          positionValue = remainingCapital * basePercent * levelMultiplier;
   335→        }
   336→        
   337→        console.log('[ReverseRatio] ULTRA SCALP position sizing:', {
   338→          level,
