   345→          percentOfStartingCapital: ((positionValue / totalAvailable) * 100).toFixed(1) + '%'
   346→        });
   347→      } else if (config.ratioMultiplier === 1) {
   348→        // Linear: 5%, 10%, 15%, 20%...
   349→        positionValue = balance * (basePercent * level);
   350→      } else {
   351→        // Exponential based on ratio multiplier
   352→        const levelRatio = Math.pow(config.ratioMultiplier, level - 1);
   353→        positionValue = balance * (basePercent * levelRatio);
   354→      }
   355→    }
   356→    
   357→    // Apply max position limit
   358→    const totalPositionValue = this.getTotalPositionValue(currentPrice);
   359→    const maxAllowed = balance * (config.maxPositionPercent / 100);
   360→    if (totalPositionValue + positionValue > maxAllowed) {
