   507→  private getAverageEntryPrice(): number {
   508→    if (this.state.positions.length === 0) return 0;
   509→    
   510→    let totalValue = 0;
   511→    let totalSize = 0;
   512→    
   513→    for (const position of this.state.positions) {
   514→      totalValue += position.entryPrice * position.size;
   515→      totalSize += position.size;
   516→    }
   517→    
   518→    return totalSize > 0 ? totalValue / totalSize : 0;
   519→  }
