// Custom zoom handler to keep right edge anchored
function handleZoom(event: WheelEvent) {
  if (!chart || !dataFeed) return;

  // Prevent default zoom behavior
  event.preventDefault();

  const timeScale = chart.timeScale();
  const visibleRange = timeScale.getVisibleRange();
  if (!visibleRange) return;

  const currentFrom = Number(visibleRange.from);
  const currentTo = Number(visibleRange.to);
  const currentRange = currentTo - currentFrom;

  // Determine zoom direction and factor
  const zoomFactor = event.deltaY > 0 ? 1.2 : 0.8; // Zoom out or in
  const newRange = currentRange * zoomFactor;

  // Get the latest data time
  const allData = dataFeed.getAllData();
  if (allData.length === 0) return;

  const latestDataTime = allData[allData.length - 1].time;
  const currentTime = Math.floor(Date.now() / 1000);

  // Keep the right edge anchored to the latest time
  const rightEdge = Math.max(latestDataTime, currentTime) + (granularityToSeconds[currentGranularity] || 60);
  const newFrom = rightEdge - newRange;

  // Apply the new range with smooth animation
  timeScale.setVisibleRange({
    from: newFrom as Time,
    to: rightEdge as Time
  });

  // Store the range for future comparison
  previousVisibleRange = { from: newFrom, to: rightEdge };
}

