    if (clockInterval) {
      clearInterval(clockInterval);
    }
    if (updateTimer) {
      clearTimeout(updateTimer);
    }
    if (reloadDebounceTimer) {
      clearTimeout(reloadDebounceTimer);
    }
    if (resizeHandler) {
      window.removeEventListener('resize', resizeHandler);
    }
    if (dataFeed) {
