private validateTimeRange(startTime: number, endTime: number): { start: number; end: number; isValid: boolean } {
  // For crypto data, we can't get data beyond the current real-world time
  // Real world max time is January 2025
  const realWorldMaxTime = Math.floor(new Date('2025-01-15T00:00:00Z').getTime() / 1000);

  // Cap the end time to real world max time
  const validEnd = Math.min(endTime, realWorldMaxTime);

  // If the requested start time is after real world time, we can't provide any data
  // But if they're asking for historical data (e.g., Dec 2024), that's valid
  if (startTime > realWorldMaxTime) {
    // Adjust start time to be within valid range
    const adjustedStart = realWorldMaxTime - 86400; // Go back 1 day from max
    return { start: adjustedStart, end: validEnd, isValid: true };
  }

  // If after capping end time, start >= end, adjust the range
  if (startTime >= validEnd) {
    // Create a valid range by going back from the valid end
    const adjustedStart = validEnd - 86400; // 1 day range
    return { start: adjustedStart, end: validEnd, isValid: true };
  }

  // Normal case: start is before end and within valid range
  return { start: startTime, end: validEnd, isValid: true };
}

