   115→  private async updateRealtimeCandle(price: number, timestamp: number) {
   116→    const granularitySeconds = this.getGranularitySeconds(this.currentGranularity);
   117→    const candleTime = Math.floor(timestamp / granularitySeconds) * granularitySeconds;
   118→    
   119→    // Get the current candle from data
   120→    const currentIndex = this.currentData.findIndex(c => c.time === candleTime);
   121→    
   122→    if (currentIndex >= 0) {
   123→      // Update existing candle
   124→      const candle = this.currentData[currentIndex];
   125→      const updated = {
   126→        ...candle,
   127→        high: Math.max(candle.high, price),
   128→        low: Math.min(candle.low, price),
   129→        close: price,
   130→        volume: candle.volume // Volume updates come from API
   131→      };
   132→      
   133→      this.currentData[currentIndex] = updated;
   134→      
   135→      // Update cache
   136→      await this.cache.updateLatestCandle(this.symbol, this.currentGranularity, updated);
   137→      
   138→      // Notify subscribers
   139→      this.subscribers.forEach(callback => callback(updated, false));
   140→    } else if (this.currentData.length > 0) {
   141→      // Check if this is a new candle
   142→      const lastCandle = this.currentData[this.currentData.length - 1];
   143→      if (candleTime > lastCandle.time) {
   144→        // Create new candle
   145→        const newCandle: CandleData = {
   146→          time: candleTime,
   147→          open: price,
   148→          high: price,
   149→          low: price,
   150→          close: price,
   151→          volume: 0
   152→        };
   153→        
   154→        this.currentData.push(newCandle);
   155→        
   156→        // Update cache
   157→        await this.cache.updateLatestCandle(this.symbol, this.currentGranularity, newCandle);
   158→        
   159→        // Notify subscribers about new candle
   160→        this.subscribers.forEach(callback => callback(newCandle, true));
   161→      }
   162→    }
   163→  }
   164→
   165→  // Get optimal granularity for a time range
   166→  getOptimalGranularity(visibleRangeSeconds: number): string {
   167→    const hours = visibleRangeSeconds / 3600;
   168→    
   169→    if (hours < 4) return '1m';        // < 4 hours: 1-minute
   170→    if (hours < 24) return '5m';       // < 1 day: 5-minute
   171→    if (hours < 168) return '1h';      // < 1 week: 1-hour
   172→    if (hours < 2160) return '6h';     // < 3 months: 6-hour
   173→    return '1D';                       // > 3 months: daily
   174→  }
   175→
   176→  // Get data for visible range with smooth transitions
   177→  async getDataForVisibleRange(startTime: number, endTime: number): Promise<CandleData[]> {
   178→    
   179→    this.visibleRange = { start: startTime, end: endTime };
   180→    
   181→    const rangeHours = (endTime - startTime) / 3600;
   182→    
   183→    // Skip auto-granularity if in manual mode
   184→    if (!this.isManualMode) {
   185→      // Find optimal granularity with hysteresis
   186→      const newGranularity = this.getOptimalGranularityWithHysteresis(rangeHours, this.activeGranularity);
   187→      
   188→      // Debounce granularity changes
   189→      if (newGranularity !== this.activeGranularity) {
   190→        this.scheduleGranularityChange(newGranularity);
   191→      }
   192→    }
   193→    
   194→    // Return current data immediately (no jarring transitions)
   195→    const data = await this.getCurrentDataForRange(startTime, endTime);
   196→    return data;
   197→  }
   198→  
   199→  // Get optimal granularity with hysteresis to prevent oscillation
   200→  private getOptimalGranularityWithHysteresis(rangeHours: number, currentGranularity: string): string {
   201→    const current = this.granularityThresholds.find(t => t.granularity === currentGranularity);
   202→    
   203→    if (!current) return this.getOptimalGranularity(rangeHours * 3600);
   204→    
   205→    // Stay with current if within overlapping zone
   206→    if (rangeHours >= current.minHours && rangeHours <= current.maxHours) {
   207→      return currentGranularity;
   208→    }
   209→    
   210→    // Find new optimal granularity
   211→    for (const threshold of this.granularityThresholds) {
   212→      if (rangeHours >= threshold.minHours && rangeHours <= threshold.maxHours) {
   213→        return threshold.granularity;
   214→      }
   215→    }
   216→    
   217→    return '1D'; // Fallback
   218→  }
   219→  
   220→  // Schedule granularity change with debouncing
   221→  private scheduleGranularityChange(newGranularity: string) {
   222→    clearTimeout(this.granularityDebounceTimer);
   223→    this.pendingGranularity = newGranularity;
   224→    
   225→    this.granularityDebounceTimer = setTimeout(() => {
   226→      this.performSmoothTransition(this.pendingGranularity!);
   227→    }, 300); // Wait 300ms for zoom to stabilize
   228→  }
   229→  
   230→  // Perform smooth transition to new granularity
   231→  private async performSmoothTransition(newGranularity: string) {
   232→    if (this.isTransitioning || this.isManualMode) return;
   233→    
   234→    console.log(`Transitioning from ${this.activeGranularity} to ${newGranularity}`);
   235→    this.isTransitioning = true;
   236→    this.targetGranularity = newGranularity;
   237→    
   238→    // Update active granularity
   239→    this.activeGranularity = newGranularity;
   240→    this.currentGranularity = newGranularity;
   241→    
   242→    // Notify UI of granularity change
   243→    this.granularityChangeCallback?.(newGranularity);
   244→    
   245→    // Preload adjacent granularities in background
   246→    this.preloadAdjacentGranularities(newGranularity);
   247→    
   248→    this.isTransitioning = false;
   249→  }

