import puppeteer from 'puppeteer';

async function testTimeframes() {
  const browser = await puppeteer.launch({ headless: false });
  const page = await browser.newPage();

  try {
    console.log('Loading dashboard...');
    await page.goto('http://localhost:5173', { waitUntil: 'networkidle2' });
    await new Promise(resolve => setTimeout(resolve, 3000));

    // Test 1Y view
    console.log('\n=== Testing 1Y View ===');

    // Find and click 1Y button
    const buttons = await page.$$('button');
    for (const button of buttons) {
      const text = await button.evaluate(el => el.textContent);
      if (text && text.trim() === '1Y') {
        await button.click();
        console.log('✓ Clicked 1Y button');
        break;
      }
    }

    await new Promise(resolve => setTimeout(resolve, 3000));

    // Get candle info
    const candleInfo = await page.evaluate(() => {
      const infoElements = document.querySelectorAll('.text-xs.text-gray-600');
      for (const el of infoElements) {
        if (el.textContent && el.textContent.includes('Visible:')) {
          return el.textContent;
        }
      }
      return 'Not found';
    });

    console.log('Candle info:', candleInfo);

    // Check console for errors
    const logs = [];
    page.on('console', msg => {
      const text = msg.text();
      if (text.includes('Error') || text.includes('429') || text.includes('future')) {
        logs.push(text);
      }
    });

    // Test 5Y view
    console.log('\n=== Testing 5Y View ===');

    const buttons2 = await page.$$('button');
    for (const button of buttons2) {
      const text = await button.evaluate(el => el.textContent);
      if (text && text.trim() === '5Y') {
        await button.click();
        console.log('✓ Clicked 5Y button');
        break;
      }
    }

    await new Promise(resolve => setTimeout(resolve, 3000));

    const candleInfo2 = await page.evaluate(() => {
      const infoElements = document.querySelectorAll('.text-xs.text-gray-600');
      for (const el of infoElements) {
        if (el.textContent && el.textContent.includes('Visible:')) {
          return el.textContent;
        }
      }
      return 'Not found';
    });

    console.log('Candle info:', candleInfo2);

    // Test zoom
    console.log('\n=== Testing Zoom ===');

    // Find chart container
    const chartContainer = await page.$('.tv-lightweight-charts');
    if (chartContainer) {
      const box = await chartContainer.boundingBox();

      // Zoom in (scroll down)
      await page.mouse.wheel({ deltaY: 100 });
      await new Promise(resolve => setTimeout(resolve, 1000));
      console.log('✓ Zoomed in');

      // Zoom out (scroll up)
      await page.mouse.wheel({ deltaY: -100 });
      await new Promise(resolve => setTimeout(resolve, 1000));
      console.log('✓ Zoomed out');
    }

    if (logs.length > 0) {
      console.log('\n⚠️  Errors detected:');
      logs.forEach(log => console.log('  -', log));
    } else {
      console.log('\n✅ No errors detected!');
    }

  } catch (error) {
    console.error('Test failed:', error);
  } finally {
    console.log('\nPress Ctrl+C to close browser...');
    // Keep browser open for manual inspection
  }
}

testTimeframes();

