   490→  export async function reloadData() {
   491→    if (!chart || !dataFeed || !candleSeries || isLoadingData) return;
   492→    
   493→    isLoadingData = true;
   494→    
   495→    try {
   496→      // Calculate time range based on period
   497→      const now = Math.floor(Date.now() / 1000);
   498→      const days = periodToDays[period] || 1;
   499→      const periodSeconds = days * 86400;
   500→      
   501→      // Get granularity for alignment
   502→      const granularitySeconds = granularityToSeconds[effectiveGranularity] || 60;
   503→      
   504→      // Align times to granularity boundaries for exact candle counts
   505→      let alignedNow = Math.floor(now / granularitySeconds) * granularitySeconds;
   506→      
   507→      // For daily candles, don't align to past midnight
   508→      if (effectiveGranularity === '1D') {
   509→        // Use current time for daily candles to include today's data
   510→        // Aligning to midnight would exclude today's candle
   511→        alignedNow = now;
   512→      }
   513→      
   514→      const startTime = alignedNow - periodSeconds;
   515→      
   516→      // Force manual mode to ensure our selected granularity is used
   517→      dataFeed.setManualGranularity(effectiveGranularity);
   518→      
   519→      // Get data for the period using ChartDataFeed API
   520→      console.log(`Loading ${period} data with ${effectiveGranularity} candles...`);
   521→      const data = await dataFeed.getDataForVisibleRange(startTime, alignedNow);
   522→      
   523→      // Calculate expected candles
   524→      const expectedCandles = Math.ceil(periodSeconds / granularitySeconds);
   525→      
   526→      console.log(`Reloading: ${data.length} candles (expected ${expectedCandles}) for ${period} with ${effectiveGranularity}`);
   527→      
   528→      // Verify candle count and update status
   529→      if (data.length === 0) {
   530→        console.warn(`⚠️ No data available yet for ${period}. Data may still be loading...`);
   531→        status = 'loading';
   532→      } else if (data.length < expectedCandles * 0.5) {
   533→        console.warn(`⚠️ Only ${data.length} of ${expectedCandles} candles loaded. Historical data is still downloading...`);
   534→        // Still show what we have but indicate loading
   535→        status = 'loading';
   536→      } else if (Math.abs(data.length - expectedCandles) > 1) {
   537→        console.warn(`⚠️ Candle count mismatch on reload! Expected ${expectedCandles}, got ${data.length}`);
   538→      } else {
   539→        status = 'connected';
   540→        errorMessage = ''; // Clear any previous errors
   541→      }
   542→      
   543→      if (data.length > 0) {
   544→        const chartData = data.map(candle => ({
   545→          ...candle,
   546→          time: candle.time as Time
   547→        }));
   548→        
   549→        // Update chart data
   550→        candleSeries.setData(chartData);
   551→        
   552→        // First fit content to ensure all data is considered
   553→        chart.timeScale().fitContent();
   554→        
   555→        // Then set the specific range we want
   556→        chart.timeScale().setVisibleRange({
   557→          from: startTime as Time,
   558→          to: alignedNow as Time
   559→        });
   560→        
   561→        // Update visible candle count
   562→        updateVisibleCandleCount(startTime, alignedNow);
   563→        
   564→        // Verify the range after a short delay
   565→        setTimeout(() => {
   566→          const actualRange = chart?.timeScale().getVisibleRange();
   567→          if (actualRange) {
   568→            // Always update with the actual visible range
   569→            updateVisibleCandleCount(Number(actualRange.from), Number(actualRange.to));
   570→          }
   571→        }, 100);
   572→      }
   573→    } catch (error: any) {
   574→      console.error('Error reloading data:', error);
   575→      if (error?.response?.status === 429) {
   576→        errorMessage = 'Rate limit exceeded. Please wait a moment and try again.';
   577→      } else {
   578→        errorMessage = `Failed to reload data: ${error.message || 'Unknown error'}`;
   579→      }
   580→    } finally {
   581→      isLoadingData = false;
   582→    }
   583→  }
