   586→  // Update visible candle count
   587→  function updateVisibleCandleCount(fromTime: number, toTime: number) {
   588→    if (!dataFeed) {
   589→      visibleCandleCount = 0;
   590→      return;
   591→    }
   592→    
   593→    // Calculate actual candle count based on granularity
   594→    const range = toTime - fromTime;
   595→    const granularitySeconds = granularityToSeconds[effectiveGranularity] || 60;
   596→    visibleCandleCount = Math.ceil(range / granularitySeconds);
   597→    
   598→    // Debug logging
   599→    // Show expected counts for common scenarios
   600→    const expectedCounts: Record<string, Record<string, number>> = {
   601→      '1H': { '1m': 60, '5m': 12, '15m': 4, '1h': 1 },
   602→      '4H': { '1m': 240, '5m': 48, '15m': 16, '1h': 4 },
   603→      '5D': { '1m': 7200, '5m': 1440, '15m': 480, '1h': 120, '6h': 20, '1d': 5 },
   604→      '1M': { '1h': 720, '6h': 120, '1d': 30 },
   605→      '3M': { '1h': 2160, '6h': 360, '1d': 90 },
   606→      '6M': { '1d': 180 },
   607→      '1Y': { '1d': 365 },
   608→      '5Y': { '1d': 1825 }
   609→    };
   610→    
   611→    const expected = expectedCounts[period]?.[effectiveGranularity];
   612→    
   613→    console.log('Visible candle count:', {
   614→      period,
   615→      granularity: effectiveGranularity,
   616→      visibleCount: visibleCandleCount,
   617→      expected: expected || 'calculated',
   618→      isCorrect: expected ? Math.abs(visibleCandleCount - expected) <= 1 : 'N/A'
   619→    });
   620→  }
   621→
   622→  // Update total candle count
   623→  async function updateTotalCandleCount() {
   624→    if (!dataFeed) return;
   625→    
   626→    try {
   627→      totalCandleCount = await dataFeed.getTotalCandleCount();
   628→    } catch (error) {
   629→      console.error('Error getting total candle count:', error);
   630→    }
   631→  }
   632→
   633→  // Update clock and countdown
   634→  function updateClock() {
   635→    const now = new Date();
   636→    const nowSeconds = Math.floor(now.getTime() / 1000);
   637→    
   638→    // Format current time
   639→    const hours = now.getHours().toString().padStart(2, '0');
   640→    const minutes = now.getMinutes().toString().padStart(2, '0');
   641→    const seconds = now.getSeconds().toString().padStart(2, '0');
   642→    currentTime = `${hours}:${minutes}:${seconds}`;
   643→    
   644→    // Calculate countdown to next candle
   645→    const granularitySeconds = granularityToSeconds[granularity] || 60;
   646→    const secondsIntoCurrentCandle = nowSeconds % granularitySeconds;
   647→    const secondsUntilNextCandle = granularitySeconds - secondsIntoCurrentCandle;
   648→    
   649→    // Format countdown
   650→    if (secondsUntilNextCandle >= 3600) {
   651→      const h = Math.floor(secondsUntilNextCandle / 3600);
   652→      const m = Math.floor((secondsUntilNextCandle % 3600) / 60);
   653→      const s = secondsUntilNextCandle % 60;
   654→      countdown = `${h}h ${m.toString().padStart(2, '0')}m ${s.toString().padStart(2, '0')}s`;
   655→    } else if (secondsUntilNextCandle >= 60) {
   656→      const m = Math.floor(secondsUntilNextCandle / 60);
   657→      const s = secondsUntilNextCandle % 60;
   658→      countdown = `${m}m ${s.toString().padStart(2, '0')}s`;
   659→    } else {
   660→      countdown = `${secondsUntilNextCandle}s`;
   661→    }
   662→  }
   663→
   664→  onDestroy(() => {
   665→    if (clockInterval) {
   666→      clearInterval(clockInterval);
   667→    }
   668→    if (updateTimer) {
   669→      clearTimeout(updateTimer);
   670→    }
   671→    if (reloadDebounceTimer) {
   672→      clearTimeout(reloadDebounceTimer);
   673→    }
   674→    if (resizeHandler) {
   675→      window.removeEventListener('resize', resizeHandler);
   676→    }
   677→    if (dataFeed) {
   678→      dataFeed.unsubscribe('chart');
   679→      dataFeed.disconnect();
   680→    }
   681→    if (chart) {
   682→      chart.remove();
   683→    }
   684→  });
   685→</script>
